	public void of_set_premi(double adec_premi)
	{
		ldec_up=0;
		ldec_sisa=0;

		if (ii_pmode == 0)
		{
			idec_up = (adec_premi / idec_add_pct ) * 1.25;
			idec_rate = 1500;
		}else{
			idec_up = (adec_premi / idec_add_pct ) * 5;
			idec_rate = 5000;
			
		}

		ldec_sisa = ( idec_up % 50 );
		if (ldec_sisa != 0) 
		{
			ldec_up = ldec_up - ldec_sisa;
		}
		of_set_up(idec_up);
		idec_premi_main = adec_premi;
	}
	
